int end = 0;
List<int> start = new();
Console.WriteLine("Hello, World!");

Console.WriteLine(input);
Console.WriteLine(input.Length.ToString());

for (int i = 0; i <= input.Length - 1; i++)
{
    if (input[i] == '(')
    {
        end += 1;
    }

    if (input[i] == ')')
    {
        end -= 1;
    }

    if (end == -1)
    {
        start.Add(i + 1);
    }
}
Console.WriteLine(end);
Console.WriteLine(start[0]);

    




